brokenGrid.css:
.broken-grid {
  display: grid;
  grid-template-columns: 1fr 2fr 1fr 3fr 2fr;
  grid-auto-rows: minmax(200px, auto);
  gap: 2px;
  background: #FF0;
  padding: 2px;
}
.broken-grid > div {
  background: #000;
  padding: 2rem;
  color: #FFF;
}

obliqueSection.css:
.oblique-section {
  transform: skewY(-5deg);
  margin: 100px 0;
  background: #FF0;
  color: #000;
}
.oblique-section > * {
  transform: skewY(5deg);
}

splitScreen.css:
.split-screen {
  display: grid;
  grid-template-columns: 1fr 2px 1fr;
  gap: 0;
  min-height: 100vh;
}
.vertical-divider {
  background: linear-gradient(to bottom, transparent, #FF0, transparent);
  width: 2px;
}

floatingCorners.css:
.corner-info {
  position: fixed;
  font-size: 10px;
  color: #666;
  z-index: 1000;
  font-family: monospace;
}
.corner-info.top-left { top: 20px; left: 20px; }
.corner-info.top-right { top: 20px; right: 20px; text-align: right; }
.corner-info.bottom-left { bottom: 20px; left: 20px; }
.corner-info.bottom-right { bottom: 20px; right: 20px; text-align: right; }

massiveText.css:
.massive-text {
  font-size: clamp(4rem, 15vw, 12rem);
  line-height: 0.8;
  font-weight: 900;
  font-family: 'Archivo Black', sans-serif;
}

glitchText.css:
.glitch {
  position: relative;
  color: white;
  font-size: 4rem;
  font-weight: bold;
  text-transform: uppercase;
}
.glitch::before,
.glitch::after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
.glitch::before {
  animation: glitch-1 0.5s infinite;
  color: #FF0;
  z-index: -1;
}
.glitch::after {
  animation: glitch-2 0.5s infinite;
  color: #0FF;
  z-index: -1;
}

asciiBorder.css:
.ascii-border {
  border: 1px solid #FF0;
  position: relative;
  padding: 1rem;
}
.ascii-border::before {
  content: '+';
  position: absolute;
  top: -8px;
  left: -8px;
  color: #FF0;
  font-size: 16px;
  font-family: monospace;
}
.ascii-border::after {
  content: '+';
  position: absolute;
  bottom: -8px;
  right: -8px;
  color: #FF0;
  font-size: 16px;
  font-family: monospace;
}

zigzagDivider.css:
.zigzag-line {
  height: 50px;
  background: 
    linear-gradient(135deg, #FF0 25%, transparent 25%) -50px 0,
    linear-gradient(225deg, #FF0 25%, transparent 25%) -50px 0,
    linear-gradient(315deg, #FF0 25%, transparent 25%),
    linear-gradient(45deg, #FF0 25%, transparent 25%);
  background-size: 100px 100px;
}

cutoutButton.css:
.cutout-btn {
  background: #FFF;
  color: #000;
  padding: 10px 20px;
  display: inline-block;
  transform: skewX(-10deg);
  border: none;
  font-weight: bold;
  cursor: crosshair;
  transition: all 0.3s;
}
.cutout-btn:hover {
  background: #FF0;
  transform: skewX(-10deg) scale(1.1);
}

floatingNav.css:
.floating-nav {
  position: fixed;
  right: 2rem;
  top: 50%;
  transform: translateY(-50%);
  z-index: 1000;
  writing-mode: vertical-lr;
}
.floating-nav a {
  display: block;
  margin: 1rem 0;
  color: #999;
  text-decoration: none;
  transition: all 0.3s;
  font-family: monospace;
}
.floating-nav a:hover {
  color: #FF0;
  letter-spacing: 2px;
}

floatingElement.css:
.floating-element {
  position: absolute;
  animation: float 20s infinite linear;
}
@keyframes float {
  0% { transform: translate(0, 0) rotate(0deg); }
  33% { transform: translate(100px, -50px) rotate(120deg); }
  66% { transform: translate(-50px, 100px) rotate(240deg); }
  100% { transform: translate(0, 0) rotate(360deg); }
}

rotatingText.css:
.rotating-text {
  animation: rotate 20s linear infinite;
}
@keyframes rotate {
  from { transform: rotate(0deg); }
  to { transform: rotate(360deg); }
}

glitchAnimation.css:
@keyframes glitch-1 {
  0% { clip-path: inset(40% 0 61% 0); transform: translate(-2px); }
  20% { clip-path: inset(92% 0 1% 0); transform: translate(2px); }
  40% { clip-path: inset(43% 0 1% 0); transform: translate(-2px); }
  60% { clip-path: inset(25% 0 58% 0); transform: translate(2px); }
  80% { clip-path: inset(54% 0 7% 0); transform: translate(-2px); }
  100% { clip-path: inset(58% 0 43% 0); transform: translate(2px); }
}
@keyframes glitch-2 {
  0% { clip-path: inset(25% 0 58% 0); transform: translate(2px); }
  20% { clip-path: inset(54% 0 7% 0); transform: translate(-2px); }
  40% { clip-path: inset(58% 0 43% 0); transform: translate(2px); }
  60% { clip-path: inset(40% 0 61% 0); transform: translate(-2px); }
  80% { clip-path: inset(92% 0 1% 0); transform: translate(2px); }
  100% { clip-path: inset(43% 0 1% 0); transform: translate(-2px); }
}

gridOverlay.css:
.grid-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: 
    linear-gradient(rgba(255,255,0,0.03) 1px, transparent 1px),
    linear-gradient(90deg, rgba(255,255,0,0.03) 1px, transparent 1px);
  background-size: 100px 100px;
  pointer-events: none;
  z-index: 1;
}
