terminalHeader.css:
.terminal-header {
  background: #000;
  color: #0F0;
  padding: 1rem;
  font-family: 'Space Mono', monospace;
  border: 1px solid #0F0;
}
.terminal-prompt { color: #FF0; }
.terminal-cursor {
  animation: blink 1s infinite;
}
@keyframes blink {
  0%, 50% { opacity: 1; }
  51%, 100% { opacity: 0; }
}

gridOverlay.css:
.grid-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image:
    linear-gradient(rgba(255,255,0,0.02) 1px, transparent 1px),
    linear-gradient(90deg, rgba(255,255,0,0.02) 1px, transparent 1px);
  background-size: 50px 50px;
  pointer-events: none;
  z-index: 1;
}

splitPanel.css:
.split-panel {
  display: grid;
  grid-template-columns: 1fr 4px 1fr;
  gap: 0;
  min-height: 400px;
}
.panel-divider {
  background: linear-gradient(to bottom, transparent, #FF0, transparent);
}
.panel-left, .panel-right {
  padding: 2rem;
  background: #0A0A0A;
  border: 1px solid #333;
}

glowText.css:
.glow-text {
  color: #FF0;
  text-shadow:
    0 0 10px #FF0,
    0 0 20px #FF0,
    0 0 30px #FF0,
    0 0 40px #FF8800;
  animation: pulse-glow 2s ease-in-out infinite;
}
@keyframes pulse-glow {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.8; }
}

typewriterText.css:
.typewriter {
  font-family: 'Space Mono', monospace;
  color: #0F0;
}
.typewriter-text {
  overflow: hidden;
  border-right: 3px solid #0F0;
  white-space: nowrap;
  animation:
    typing 3s steps(40, end),
    cursor 1s step-end infinite;
}
@keyframes typing {
  from { width: 0; }
  to { width: 100%; }
}
@keyframes cursor {
  from, to { border-color: transparent; }
  50% { border-color: #0F0; }
}

dataLabel.css:
.data-label {
  font-family: 'Space Mono', monospace;
  font-size: 0.875rem;
}
.label-key {
  color: #888;
  margin-right: 0.5rem;
}
.label-value {
  color: #FF0;
  font-weight: bold;
}

warningText.css:
.warning-text {
  color: #F00;
  font-weight: bold;
  font-family: 'Space Mono', monospace;
  padding: 0.5rem 1rem;
  background: rgba(255,0,0,0.1);
  border: 2px solid #F00;
  animation: warning-pulse 1s infinite;
}
@keyframes warning-pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.6; }
}

asciiBorder.css:
.ascii-border {
  position: relative;
  border: 1px dashed #FF0;
  padding: 2rem;
  margin: 2rem 0;
}
.ascii-border::before {
  content: '[===================]';
  position: absolute;
  top: -10px;
  left: 20px;
  background: #000;
  color: #FF0;
  padding: 0 10px;
  font-family: monospace;
}

timelineBlock.css:
.timeline-block {
  display: grid;
  grid-template-columns: 100px 1fr;
  gap: 20px;
  padding: 1rem 0;
  border-left: 4px solid #FF0;
  margin-left: 50px;
}
.timeline-time {
  color: #FF0;
  font-weight: bold;
  font-family: 'Space Mono', monospace;
  font-size: 1.2rem;
}
.timeline-content {
  padding-left: 20px;
}
.timeline-block.urgent .timeline-time {
  color: #F00;
  animation: blink 1s infinite;
}

statusIndicator.css:
.status-indicator {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  font-family: 'Space Mono', monospace;
  font-size: 0.875rem;
}
.status-dot {
  width: 10px;
  height: 10px;
  border-radius: 50%;
  background: #888;
}
.status-indicator.active .status-dot {
  background: #0F0;
  box-shadow: 0 0 10px #0F0;
  animation: pulse 2s infinite;
}
.status-indicator.error .status-dot {
  background: #F00;
  box-shadow: 0 0 10px #F00;
}
@keyframes pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.5; }
}

dataCard.css:
.data-card {
  background: #0A0A0A;
  border: 1px solid #333;
  margin: 1rem 0;
}
.data-card-header {
  background: repeating-linear-gradient(
    45deg,
    #FF0,
    #FF0 10px,
    #000 10px,
    #000 20px
  );
  color: #000;
  padding: 0.5rem 1rem;
  font-weight: bold;
  font-family: 'Space Mono', monospace;
}
.data-card-body {
  padding: 1rem;
}
.data-row {
  display: flex;
  justify-content: space-between;
  padding: 0.5rem 0;
  border-bottom: 1px dotted #333;
  font-family: monospace;
}
.data-row span:last-child {
  color: #FF0;
}

primaryButton.css:
.btn-primary {
  background: #FF0;
  color: #000;
  border: none;
  padding: 1rem 2rem;
  font-family: 'Space Mono', monospace;
  font-weight: bold;
  font-size: 1rem;
  cursor: pointer;
  transform: skewX(-10deg);
  transition: all 0.3s;
  text-transform: uppercase;
}
.btn-primary:hover {
  background: #F00;
  color: #FFF;
  transform: skewX(-10deg) scale(1.05);
  box-shadow: 0 0 20px rgba(255,0,0,0.5);
}

secondaryButton.css:
.btn-secondary {
  background: transparent;
  color: #FF0;
  border: 2px solid #FF0;
  padding: 0.75rem 1.5rem;
  font-family: 'Space Mono', monospace;
  font-weight: bold;
  cursor: pointer;
  transition: all 0.3s;
  text-transform: uppercase;
}
.btn-secondary:hover {
  background: #FF0;
  color: #000;
  box-shadow: 0 0 15px rgba(255,255,0,0.5);
}

dangerButton.css:
.btn-danger {
  background: #F00;
  color: #FFF;
  border: 2px solid #F00;
  padding: 1rem 2rem;
  font-family: 'Space Mono', monospace;
  font-weight: bold;
  cursor: pointer;
  animation: danger-pulse 1s infinite;
  text-transform: uppercase;
}
@keyframes danger-pulse {
  0%, 100% {
    background: #F00;
    box-shadow: 0 0 0 0 rgba(255,0,0,0.7);
  }
  50% {
    background: #CC0000;
    box-shadow: 0 0 0 10px rgba(255,0,0,0);
  }
}

dataTable.css:
.data-table {
  width: 100%;
  border-collapse: collapse;
  font-family: 'Space Mono', monospace;
  background: #0A0A0A;
}
.data-table thead {
  background: repeating-linear-gradient(
    45deg,
    #FF0,
    #FF0 10px,
    #000 10px,
    #000 20px
  );
}
.data-table th {
  color: #000;
  padding: 0.75rem;
  text-align: left;
  font-weight: bold;
}
.data-table td {
  border: 1px solid #333;
  padding: 0.75rem;
  color: #CCC;
}
.data-table .status-active {
  color: #0F0;
}
.data-table .status-error {
  color: #F00;
}
.data-table tr:hover {
  background: #1A1A1A;
}

scoreGrid.css:
.score-grid {
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 1px;
  background: #FF0;
  border: 2px solid #FF0;
}
.score-header {
  background: #FF0;
  color: #000;
  padding: 0.75rem;
  font-weight: bold;
  font-family: 'Space Mono', monospace;
}
.score-item, .score-value {
  background: #000;
  padding: 0.75rem;
  color: #FFF;
}
.score-value {
  text-align: center;
  color: #FF0;
  font-weight: bold;
}

scanLine.css:
.scan-container {
  position: relative;
  overflow: hidden;
  padding: 2rem;
  background: #0A0A0A;
  border: 1px solid #333;
}
.scan-line {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(to right, transparent, #0F0, transparent);
  animation: scan 3s linear infinite;
}
@keyframes scan {
  0% { transform: translateY(0); }
  100% { transform: translateY(200px); }
}

glitchEffect.css:
.glitch {
  position: relative;
  color: #FFF;
  font-size: 3rem;
  font-weight: bold;
  text-transform: uppercase;
}
.glitch::before,
.glitch::after {
  content: attr(data-text);
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
}
.glitch::before {
  animation: glitch-1 0.5s infinite;
  color: #F00;
  z-index: -1;
}
.glitch::after {
  animation: glitch-2 0.5s infinite;
  color: #0FF;
  z-index: -1;
}
@keyframes glitch-1 {
  0% { clip-path: inset(40% 0 61% 0); transform: translate(-2px); }
  20% { clip-path: inset(92% 0 1% 0); transform: translate(2px); }
  40% { clip-path: inset(43% 0 1% 0); transform: translate(-2px); }
  60% { clip-path: inset(25% 0 58% 0); transform: translate(2px); }
  80% { clip-path: inset(54% 0 7% 0); transform: translate(-2px); }
  100% { clip-path: inset(58% 0 43% 0); transform: translate(2px); }
}
@keyframes glitch-2 {
  0% { clip-path: inset(25% 0 58% 0); transform: translate(2px); }
  20% { clip-path: inset(54% 0 7% 0); transform: translate(-2px); }
  40% { clip-path: inset(58% 0 43% 0); transform: translate(2px); }
  60% { clip-path: inset(40% 0 61% 0); transform: translate(-2px); }
  80% { clip-path: inset(92% 0 1% 0); transform: translate(2px); }
  100% { clip-path: inset(43% 0 1% 0); transform: translate(-2px); }
}

matrixRain.css:
.matrix-rain {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  overflow: hidden;
  pointer-events: none;
  z-index: -1;
}
.matrix-rain span {
  position: absolute;
  color: #0F0;
  font-family: monospace;
  font-size: 20px;
  animation: fall linear infinite;
}
@keyframes fall {
  to { transform: translateY(100vh); }
}
