fixedNav.css:
.literary-nav {
  position: fixed;
  top: 0;
  width: 100%;
  background: rgba(254, 254, 248, 0.95);
  backdrop-filter: blur(10px);
  z-index: 1000;
  padding: 1.5rem 0;
  box-shadow: 0 1px 0 rgba(10, 14, 39, 0.1);
}
.nav-container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 2rem;
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.nav-title {
  font-size: 1.2rem;
  font-weight: 700;
  letter-spacing: -0.02em;
}
.nav-menu {
  display: flex;
  gap: 2rem;
}
.nav-menu a {
  color: #0A0E27;
  text-decoration: none;
  position: relative;
  transition: color 0.3s ease;
}
.nav-menu a::after {
  content: '';
  position: absolute;
  bottom: -4px;
  left: 0;
  width: 0;
  height: 2px;
  background: #00F0FF;
  transition: width 0.3s ease;
}
.nav-menu a:hover::after {
  width: 100%;
}

literaryHero.css:
.hero {
  min-height: 100vh;
  display: flex;
  align-items: center;
  padding: 0 2rem;
  margin-top: 80px;
}
.hero-content {
  max-width: 1400px;
  margin: 0 auto;
  width: 100%;
}
.hero-label {
  display: inline-block;
  background: #0A0E27;
  color: #FEFEF8;
  padding: 0.5rem 1.5rem;
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 0.1em;
  margin-bottom: 2rem;
  transform: rotate(-1deg);
}
.hero-title {
  font-size: clamp(3rem, 8vw, 6rem);
  font-weight: 900;
  line-height: 0.9;
  margin-bottom: 2rem;
  letter-spacing: -0.03em;
}
.hero-title span {
  display: block;
  color: #00F0FF;
  font-size: 0.7em;
  margin-top: 0.2em;
}
.hero-description {
  font-size: 1.25rem;
  max-width: 800px;
  margin-bottom: 3rem;
  line-height: 1.8;
  font-weight: 300;
}

numberedCard.css:
.literary-card {
  background: #FEFEF8;
  border: 2px solid #0A0E27;
  padding: 2rem;
  position: relative;
  transition: all 0.3s ease;
  cursor: pointer;
}
.literary-card::before {
  content: '';
  position: absolute;
  top: 4px;
  left: 4px;
  width: 100%;
  height: 100%;
  background: #0A0E27;
  z-index: -1;
  transition: all 0.3s ease;
}
.literary-card:hover {
  transform: translate(-4px, -4px);
  background: #0A0E27;
  color: #FEFEF8;
}
.literary-card:hover::before {
  top: 8px;
  left: 8px;
}
.literary-card:hover .card-number {
  background: #FEFEF8;
  color: #0A0E27;
}
.card-number {
  display: inline-block;
  width: 40px;
  height: 40px;
  background: #00F0FF;
  color: #0A0E27;
  font-family: 'Space Grotesk', monospace;
  font-weight: 700;
  font-size: 1.2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 1.5rem;
  transition: all 0.3s ease;
}
.card-title {
  font-size: 1.5rem;
  font-weight: 700;
  margin-bottom: 1rem;
  line-height: 1.2;
}
.card-description {
  font-size: 0.95rem;
  opacity: 0.8;
  line-height: 1.7;
}

phaseCard.css:
.phase-card {
  background: linear-gradient(to right, #FEFEF8 50%, transparent 50%);
  background-size: 20px 1px;
  background-repeat: repeat-x;
  background-position: 0 100%;
  padding-bottom: 2rem;
  margin-bottom: 3rem;
}
.phase-header {
  display: flex;
  align-items: baseline;
  gap: 2rem;
  margin-bottom: 2rem;
}
.phase-number {
  font-family: 'Space Grotesk', monospace;
  font-size: 5rem;
  font-weight: 700;
  line-height: 1;
  color: #7C3AED;
  opacity: 0.2;
}
.phase-title {
  font-size: 2rem;
  font-weight: 700;
}
.phase-description {
  font-size: 1rem;
  line-height: 1.7;
}

scheduleTable.css:
.schedule-table {
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
  margin-top: 3rem;
}
.schedule-table th {
  background: #0A0E27;
  color: #FEFEF8;
  padding: 1rem;
  text-align: left;
  font-weight: 500;
  font-size: 0.9rem;
  letter-spacing: 0.05em;
}
.schedule-table td {
  padding: 1.25rem 1rem;
  border-bottom: 1px solid rgba(0,0,0,0.1);
  vertical-align: top;
}
.schedule-table tr:hover {
  background: rgba(0,240,255,0.05);
}
.schedule-session {
  font-family: 'Space Grotesk', monospace;
  font-weight: 700;
  color: #00F0FF;
}

literarySection.css:
.literary-section {
  max-width: 1400px;
  margin: 80px auto;
  padding: 0 2rem;
}
.section-label {
  font-family: 'Space Grotesk', monospace;
  font-size: 0.8rem;
  font-weight: 700;
  letter-spacing: 0.2em;
  opacity: 0.5;
  margin-bottom: 1rem;
  text-transform: uppercase;
}
.section-title {
  font-size: 2.5rem;
  font-weight: 700;
  margin-bottom: 3rem;
  position: relative;
  padding-bottom: 1rem;
}
.section-title::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: 0;
  width: 60px;
  height: 3px;
  background: #00F0FF;
}
.section-content {
  margin-top: 2rem;
}

gridLayout.css:
.literary-grid {
  display: grid;
  gap: 2rem;
}
.grid-2 {
  grid-template-columns: repeat(auto-fit, minmax(400px, 1fr));
}
.grid-3 {
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
}
.grid-4 {
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
}
@media (max-width: 768px) {
  .grid-2, .grid-3, .grid-4 {
    grid-template-columns: 1fr;
  }
}

paperTexture.css:
body::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: 
    repeating-linear-gradient(
      90deg,
      transparent,
      transparent 2px,
      rgba(10, 14, 39, 0.01) 2px,
      rgba(10, 14, 39, 0.01) 4px
    );
  pointer-events: none;
  z-index: 1;
}

shadowCard.css:
.shadow-card {
  position: relative;
}
.shadow-card::before {
  content: '';
  position: absolute;
  top: 4px;
  left: 4px;
  width: 100%;
  height: 100%;
  background: #0A0E27;
  z-index: -1;
  transition: all 0.3s ease;
}
.shadow-card:hover::before {
  top: 8px;
  left: 8px;
}

invertHover.css:
.invert-hover {
  background: #FEFEF8;
  color: #0A0E27;
  transition: all 0.3s ease;
}
.invert-hover:hover {
  background: #0A0E27;
  color: #FEFEF8;
  transform: translate(-4px, -4px);
}

literaryHeading.css:
.literary-heading {
  font-family: 'Zen Kaku Gothic New', sans-serif;
  font-size: clamp(2rem, 5vw, 4rem);
  font-weight: 700;
  line-height: 1.2;
  letter-spacing: -0.02em;
  color: #0A0E27;
}

accentNumber.css:
.accent-number {
  font-family: 'Space Grotesk', monospace;
  font-weight: 700;
  font-size: 1.2rem;
  color: #00F0FF;
  display: inline-block;
  min-width: 2ch;
  text-align: center;
}

rotatedLabel.css:
.rotated-label {
  display: inline-block;
  background: #0A0E27;
  color: #FEFEF8;
  padding: 0.5rem 1.5rem;
  font-size: 0.8rem;
  font-weight: 500;
  letter-spacing: 0.1em;
  text-transform: uppercase;
  transform: rotate(-1deg);
}

fadeInUp.css:
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}
.fade-in-up {
  animation: fadeInUp 0.8s ease-out;
}

underlineSlide.css:
.underline-slide {
  position: relative;
}
.underline-slide::after {
  content: '';
  position: absolute;
  bottom: -4px;
  left: 0;
  width: 0;
  height: 2px;
  background: #00F0FF;
  transition: width 0.3s ease;
}
.underline-slide:hover::after {
  width: 100%;
}
